import React, { useRef } from "react";
import { useDispatch } from "react-redux";
import { createNote } from "../redux/slices/practiseSlice";

function Practise1() {
  const inputRefValue = useRef(null);
  const dispatch = useDispatch();

  function handleSubmit(e) {
    e.preventDefault();

    const refValue = inputRefValue.target.value;
    dispatch(createNote(refValue));
  }
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" ref={inputRefValue} />
        <input type="submit" onSubmit={handleSubmit} />
      </form>
    </div>
  );
}

export default Practise1;
